import React, { useState } from 'react';
import { AiOutlineEye, AiOutlineEyeInvisible } from 'react-icons/ai';
import { toast } from 'react-toastify'; // Include ToastContainer
import './Signup.css'; // Import your CSS file

export const Signup = () => {
  const [showCreatePass, setShowCreatePass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);
  const [emailError, setEmailError] = useState(false); // Track email validation error

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Email validation regex

  function changeHandler(event) {
    const { name, value } = event.target;

    if (name === "email") {
      setEmailError(!emailRegex.test(value)); // Set error if email is invalid
    }

    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  }

  async function postdata_into_database(e) {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast.error("Passwords do not match");
      return;
    }

    if (emailError || !emailRegex.test(formData.email)) {
      toast.error("Invalid email format");
      return;
    }

    const { firstName, lastName, email, password, confirmPassword } = formData;

    try {
      // Check if email already exists
      const checkRes = await fetch("api/v1/checkuser", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ e_mail: email }), // Ensure matching backend format
      });

      const checkData = await checkRes.json();

      if (checkData.exists) {
        toast.error("User already exists with this email");
        return;
      }

      // If user doesn't exist, proceed with registration
      const res = await fetch("api/v1/createmessage", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          first_name: firstName, // Ensure backend expects `first_name`
          last_name: lastName,   // Ensure backend expects `last_name`
          e_mail: email,         // Ensure backend expects `e_mail`
          password,
          confirm_password: confirmPassword, // Ensure backend expects `confirm_password`
        }),
      });

      const data = await res.json();

      if (data.success) {
        toast.success("Account Created");
      } else {
        toast.error("Error creating account");
      }

    } catch (error) {
      toast.error("Error connecting to the server");
    }
  }

  return (
    <>
      <div className='heading'>Sign Up for the Register</div>
      <div className="signup-container">
        <form method="POST" onSubmit={postdata_into_database}>
          <div className="form-group">
            <label className="form-label">
              First Name <sup className="required">*</sup>
            </label>
            <input
              type="text"
              required
              placeholder="Enter First Name"
              onChange={changeHandler}
              value={formData.firstName}
              name="firstName"
              className="form-input"
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              Last Name <sup className="required">*</sup>
            </label>
            <input
              type="text"
              required
              placeholder="Enter Last Name"
              onChange={changeHandler}
              value={formData.lastName}
              name="lastName"
              className="form-input"
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              Email Address <sup className="required">*</sup>
            </label>
            <input
              type="email"
              required
              placeholder="Enter your email address"
              value={formData.email}
              onChange={changeHandler}
              name="email"
              className={`form-input ${emailError ? "error" : ""}`} // Add error class if email is invalid
            />
            {emailError && <span className="error-text">Email is not valid</span>} {/* Show error text */}
          </div>

          <div className="form-group password-group">
            <label className="form-label">
              Create Password <sup className="required">*</sup>
            </label>
            <input
              type={showCreatePass ? "text" : "password"}
              required
              placeholder="Enter Password"
              onChange={changeHandler}
              value={formData.password}
              name="password"
              className="form-input"
            />
            <span
              onClick={() => setShowCreatePass(!showCreatePass)}
              className="password-toggle"
            >
              {showCreatePass ? (
                <AiOutlineEyeInvisible fontSize={24} fill="#AFB2BF" />
              ) : (
                <AiOutlineEye fontSize={24} fill="#AFB2BF" />
              )}
            </span>
          </div>

          <div className="form-group password-group">
            <label className="form-label">
              Confirm Password <sup className="required">*</sup>
            </label>
            <input
              type={showConfirmPass ? "text" : "password"}
              required
              placeholder="Confirm Password"
              onChange={changeHandler}
              value={formData.confirmPassword}
              name="confirmPassword"
              className="form-input"
            />
            <span
              onClick={() => setShowConfirmPass(!showConfirmPass)}
              className="password-toggle"
            >
              {showConfirmPass ? (
                <AiOutlineEyeInvisible fontSize={24} fill="#AFB2BF" />
              ) : (
                <AiOutlineEye fontSize={24} fill="#AFB2BF" />
              )}
            </span>
          </div>

          <button type="submit" className="submit-button">
            Create Account
          </button>
        </form>
      </div>
    </>
  );
};
